{
    "name": "{{projectName}}_{{$randomUUID}}", // Name of project
    "organizationId": {{organizationId}} // Organization ID where project is created
}

{
  "snapshotId": {{snapshotId}}, // Snapshot (version) ID of the parent goal retrieved from the api/goals/[goalId]/versions endpoint
  "goalId": {{goalId}}, // ID of the parent goal
  "title": "Checkpoint_{{$randomUUID}}" // Name of the checkpoint with a random UUID to prevent duplicate names
}

{
  "checkpointId": {{checkpointId}}, // ID of the checkpoint to be attached
  "position": {{checkpointPosition}} // Sequence position in the journey, defined the run order with the lowest number in the sequence being run first
}
